#!/bin/bash

str=""

# loop through each character
for ((i=0; i<${#str}; i++))
do
    a=${str:$i:1}
    b=${str:$i+1:1}
    c=${str:$i+2:1}
    d=${str:$i+3:1}
    # Check if the character is unique
    if [ "$a" != "$b" ] && [ "$a" != "$c" ] && [ "$a" != "$d" ] && [ "$b" != "$c" ] && [ "$b" != "$d" ] && [ "$c" != "$d" ]; then
        echo $a, $b, $c, $d
        echo $(($i+4))
        break
    fi
done